import { useState } from "react";
import './Comments.css';

export default function Comments() {
    const [comment, setComment] = useState("");

    function handleInput(event) {
        setComment(event.targe.value)
        console.log(event.targe.value)
    }

    function handleSubmit(event) {
        event.preventDefault()
    }
    return (
        <div className='commentsDiv'>
            <form onSubmit={handleSubmit}>
                <label htmlFor="username">Commenting as</label>
                <input type="text" id="username"/>
                <input  onChange={handleInput} className='addComment' type="text" placeholder="Add a comment..." value={comment} />
                <span className='focus-border'></span>
                <input type="submit" value="comment" id="submitComment"/>
            </form>
        </div>
    )
}
